"use strict";

var modalExit = document.getElementById("modal-exit");
var modalPost = document.getElementById("modal-post");
var modalCreate = document.getElementById("modal-create");
var modalCreateExit = document.getElementById("modal-create-exit");
var body = document.body;
modalExit.addEventListener("click", function (e) {
  modalPost.classList.remove("backdrop--show");
  body.style.overflow = "auto";
});

function showModal() {
  modalPost.classList.add("backdrop--show");
  body.style.overflow = "hidden";
}

modalCreateExit.addEventListener("click", function (e) {
  modalCreate.classList.remove("backdrop--show");
  body.style.overflow = "auto";
});

function showModalCreate() {
  modalCreate.classList.add("backdrop--show");
  body.style.overflow = "hidden";
}