"use strict";

var fileInput = document.getElementById("file-input");
var fileButton = document.getElementById("file-btn");
var mediaContainer = document.getElementById("media-container");
fileButton.addEventListener("click", function () {
  fileInput.click();
});
fileInput.addEventListener("change", function (e) {
  if (!e.target.files.length) return;
  var reader = new FileReader();
  var file = e.target.files[0];

  reader.onload = function (e) {
    if (file.type.includes("image")) {
      mediaContainer.innerHTML = "<img\n        src=\"".concat(e.target.result, "\"\n        alt=\"Post image\"\n        draggable=\"false\"\n      />");
    } else if (file.type.includes("video")) {
      mediaContainer.innerHTML = " <video src=\"".concat(e.target.result, "\" controls></video>");
    }
  };

  reader.readAsDataURL(e.target.files[0]);
});